if [ ! -f "$dir/qshar" ]
then
	$write "(!)ERROR(!). Nastepuje kontynuacja.\n"
else
	$write "OK, MD5 CHECK -> "
	# Przyznanie plikowi wlasciwych atrybutow
	chmod 755 "$dir/qshar"
	# Sprawdzenie zgodnosci sumy kontrolnej (jezeli jest) zawartosci utworzonego pliku z oryginalna.
	if [ ! -z "$md5" ]
	then
		val=$($md5 $dir/qshar)
		# Obciecie stringa do 32 znakow.
		hash=${val:0:32}
		if [ "$hash" == "0b95169e24e0204b9a2afd11665cbc5a" ]
		then
			$write "OK\n"
		else
			$write "(!)WRONG(!)\n"
		fi
	else
		$write "(?)MISSING(?)\n"
	fi
fi

