# Sprawdzenie, czy plik sie utworzyl.
if [ ! -f "$dir/qshar.1" ]
then
	$write "(!)ERROR(!). Nastepuje kontynuacja.\n"
else
	$write "OK, MD5 CHECK -> "
	# Przyznanie plikowi wlasciwych atrybutow
	chmod 644 "$dir/qshar.1"
	# Sprawdzenie zgodnosci sumy kontrolnej (jezeli jest) zawartosci utworzonego pliku z oryginalna.
	if [ ! -z "$md5" ]
	then
		val=$($md5 $dir/qshar.1)
		# Obciecie stringa do 32 znakow.
		hash=${val:0:32}
		if [ "$hash" == "5ba6f654148af3929264f69f042c9982" ]
		then
			$write "OK\n"
		else
			$write "(!)WRONG(!)\n"
		fi
	else
		$write "(?)MISSING(?)\n"
	fi
fi

